import AboutContent from "../components/about_components/AboutContent";
import Header from "../components/Header";
import Footer from "../components/Footer";
import sideBarImg from "../images/sidebarimg.jpeg";

const About = () => {
  const sideBarStyle = {
    backgroundImage: "url(" + sideBarImg + ")",
  };
  return (
    <>
      <section
        style={{ minHeight: "calc(100vh - 60px)" }}
        className="main-about-section"
      >
        <Header />
        <div className="about-content-area lg:mt-8 md:mt-4 lg:flex-row flex flex-col gap-4 ml-5 mr-5 md:ml-10 md:mr-10 lg:mr-20 lg:ml-20">
          <div
            className="backgroundSidebarImg w-full h-32 lg:w lg:h-auto bg-cover rounded mt-4 lg:mt-0 lg:mb-4 bg-center"
            style={sideBarStyle}
          ></div>
          <div className="main-about-content text-slate-50 bg-amber-600 rounded pt-4 pb-8 mb-4">
            <AboutContent />
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default About;
