import { Link } from "react-router-dom";
import { useState } from "react";
// importing icons
import { faBars, faXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Header = ({ Paths }) => {
  const [menu, setMenu] = useState("hidden");
  const [menuIcon, setMenuIcon] = useState(faBars);

  const menuRef = document.querySelector(".link-area");
  const menuToggle = (e) => {
    menu === "hidden" ? setMenu("flex") : setMenu("hidden");
    if (menu === "flex") {
      setMenuIcon(faBars);
    } else if (menu === "hidden") {
      setMenuIcon(faXmark);
    }
  };

  return (
    <nav className="header-area bg-amber-600 text-slate-50 flex justify-between items-center pt-1 pb-1">
      <h1 className="font-bold text-md md:text-xl lg:text-3xl ml-10 lg:ml-20">
        Iman & Associates Ltd.
      </h1>
      <FontAwesomeIcon
        icon={menuIcon}
        className="mr-10 menu-icon lg:hidden md:hidden"
        size="2x"
        onClick={menuToggle}
      />
      {/* styling for resposive navbar */}
      <div
        className={`link-area flex flex-col z-10 absolute top-10 right-5 items-center bg-yellow-600 p-4 rounded border-yellow-500 border-4 md:flex md:relative ${menu}`}
      >
        <ul className="text-lg text-center mb-4 flex flex-col">
          <li className="mb-2">
            <Link to={Paths.home}>Home</Link>
          </li>
          <li className="mb-2">
            <Link to={Paths.about}>About</Link>
          </li>
          <li>
            <Link to={Paths.contacts}>Contact</Link>
          </li>
        </ul>
        <button className="btn-primary bg-sky-600	hover:bg-sky-700 pr-4 pl-4 pt-2 pb-2 rounded	text-xl">
          Sign In
        </button>
      </div>
    </nav>
  );
};
export default Header;
